import React, { useState } from "react";
import { GetStaticProps, GetStaticPaths, GetServerSideProps } from "next";
import marked from "marked";

export default function HealthArticle(props) {
	console.log(props.data);
	const renderer = new marked.Renderer();
	const content = `   
<u></u>   
		<div class="banner-image">![banner](/static/media/banner.b58d195e.png)</div>

> #  If You Really Want to Optimize Your Diet, Focus on Fiber
<div class="profile-img">![banner](data:image/png;base64,)</div> | <div class="profile-name">Arjun Sharma</div><div class="profile-role">Diet Expert</div> |
---| ---|
|   |

<u></u>
 		<div class="content-text">There are many cohort studies that have shown people who eat less fiber are at high risk for developing Crohn’s disease,” Kaplan says. One of the most robust of these studies was a 2013 report from researchers at Harvard Medical School that looked at diet and health data collected from roughly 170,000 women over a period of more than 26 years. It found that those women who landed in the top fifth of the population in terms of fiber intakes were 40% less likely to have been diagnosed with Crohn’s disease than women who landed in the bottom fifth.
</div>
`;
	const [test, setTest] = useState("");
	const Previewer = props => {
		return (
			<div
				id="previewer"
				dangerouslySetInnerHTML={{
					__html: marked(props.markdown, { renderer: renderer })
				}}
			/>
		);
	};
	const Previewertest = props => {
		return (
			<div
				id="previewer"
				dangerouslySetInnerHTML={{
					__html: marked(props.markdown, { renderer: renderer })
				}}
			/>
		);
	};
	React.useEffect(() => {
		getContent();
	}, []);
	async function getContent() {
		const res = await fetch(
			`https://run.mocky.io/v3/b5680edc-7e03-4d2d-9cb6-147e63fe673f`,
			{
				method: "GET",
				headers: { "Content-Type": "application/json" }
			}
		);
		console.log(res);
		const result = await res.json();
		setTest(result.blog_content);
		console.log(result.blog_content);
	}

	return (
		<div>
			<Previewer markdown={props.content} />
			{/* <Previewertest markdown={test} /> */}
			<div>{props.content}</div>
		</div>
	);
}

export const getStaticProps = async (ctx: {
	blog_id: string;
	blog_content: string;
}) => {
	// ...
	const res = await fetch(
		"https://run.mocky.io/v3/b5680edc-7e03-4d2d-9cb6-147e63fe673f"
	);
	const data = await res.json();
	return {
		props: { id: data.blog_id, content: data.blog_content }
	};
};
